//Tarjeta de personaje

import { Image, Modal, Pressable, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { Episode } from "./episodesType"
import { useState } from "react";

//tipo para especificar las funciones de characterCard
//propiedades de Character card 
//primer paso definir props 
type Props = {
    episode: Episode
}
export function EspisodesCard({ episode }: Props) {
    const [modalVisible, setModalVisible] = useState(false);
    //estructura de la tarjeta 
    return (
        <View>
            <TouchableOpacity style={styles.card} onPress={() => setModalVisible(true)}>
                <View style={styles.card}>

                    <Image
                        style={styles.image}
                        source={{
                            uri: "data:image/jpeg;base64,"
                        }} />

                    <View style={styles.content}>

                        <View>
                            <Text style={styles.label}>Nombre:</Text>
                            <Text style={[styles.textValue, episode.name.length > 20 ? styles.smallText : undefined]}>{episode.name}</Text>
                        </View>

                        <View>
                            <Text style={styles.label}>Fecha de estreno: </Text>

                            <View style={styles.row}>
                                <Text style={styles.textValue}>{episode.air_date}</Text>
                            </View>

                        </View>

                        <View>
                            <Text style={styles.label}>Episodio:</Text>
                            <Text style={[styles.textValue,
                            episode.episode.length > 20 ? styles.smallText : undefined
                            ]}>{episode.episode}</Text>
                        </View>

                        <View>
                            <Text style={styles.label}>Fecha de creación:</Text>
                            <Text style={[styles.textValue,
                            episode.created.length > 20 ? styles.smallText : undefined
                            ]}>{episode.created}</Text>
                        </View>

                        <View>
                            <Text style={styles.label}>Personajes que participaron:</Text>
                            <Text style={[styles.textValue,
                            episode.created.length > 20 ? styles.smallText : undefined
                            ]}>{episode.characters.length}</Text>
                        </View>
                    </View>
                </View>
            </TouchableOpacity>
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => setModalVisible(false)}
            >
                <View style={styles.modalContainer}>
                    <View style={styles.modalContent}>
                        <Image
                            style={styles.modalImage}
                            source={{
                                uri: "data:image/jpeg;base64,"
                            }} />

                        <Text style={styles.modalTitle}>{episode.name}</Text>
                        <Text style={styles.modalText}>Fecha de estreno: {episode.air_date}</Text>
                        <Text style={styles.modalText}>Episodio: {episode.episode}</Text>
                        <Text style={styles.modalText}>Fecha de creación:{episode.created}</Text>
                        <Text style={styles.modalText}>Personajes que participaron: {episode.characters.length}</Text>

                        <Pressable onPress={() => setModalVisible(false)} style={styles.closeButton}>
                            <Text style={styles.textWhite}>Cerrar</Text>
                        </Pressable>
                    </View>
                </View>
            </Modal>

        </View>
    );
}

const styles = StyleSheet.create({
    card: {
        display: "flex",
        flexDirection: "row",
        width: "100%",
        height: "auto",
        borderRadius: 8,
        borderWidth: 4,
        borderColor: "#aa853c",
        marginVertical: 5,
        backgroundColor: "#c1c19f",

    },
    image: {
        width: "40%",
        height: "100%",
        objectFit: "cover"
    },
    content: {
        display: "flex",
        flexDirection: "column",
        marginLeft: 10,
    },
    label: {
        color: "#2d7096",
        fontSize: 16,
    },
    textValue: {
        color: "#094161",
        fontSize: 20,
        fontWeight: 700,
    },
    smallText: {
        fontSize: 13,
    },
    row: {
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        gap: 5,
    },
    modalContainer: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "rgba(0,0,0,0.5)",
    },
    modalContent: {
        width: 300,
        backgroundColor: "white",
        padding: 20,
        borderRadius: 10,
        alignItems: "center",
        justifyContent: "center",
    },
    modalImage: {
        width: 120,
        height: 120,
        borderRadius: 10,
        marginBottom: 10,
    },
    modalTitle: {
        fontSize: 20,
        fontWeight: "bold",
        marginBottom: 5,
    },
    modalText: {
        fontSize: 16,
        marginBottom: 5,
    },
    closeButton: {
        marginTop: 15,
        backgroundColor: "#094161",
        padding: 10,
        borderRadius: 8,
    },
    textWhite: {
        color: "white",
        fontWeight: "bold",
    },

});